import psycopg2
from psycopg2.extras import RealDictCursor
from flask import g, current_app

def init_db():
    current_app.teardown_appcontext(close_db)

def get_db():
    if 'db' not in g:
        g.db = psycopg2.connect(
            host='localhost',
            database='flask_tutorial',
            user='postgres',
            password='enter123'
        )
        g.db.cursor_factory = RealDictCursor
    return g.db

def close_db(e=None):
    db = g.pop('db', None)
    if db is not None:
        db.close()

def fetch_secret_key(name):
    db_conn = get_db()
    cursor = db_conn.cursor()
    cursor.execute("SELECT key FROM public.\"Keys\" WHERE name=%s", (name,))
    secret_key = cursor.fetchone()
    cursor.close()
    return secret_key['key']